import React, { useState } from 'react';
import { Button, TextField, Box } from '@mui/material';

function ConnectSessionForm() {
    const [nickname, setNickname] = useState('');
    const [sessionId, setLobby] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = { nickname, sessionId };

        // Здесь вы делаете запрос на сервер
        fetch('http://localhost:3001/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // Обработайте ответ от сервера здесь
            })
            .catch(error => {
                console.error('Ошибка:', error);
            });
    };

    return (
        <Box component="form" onSubmit={handleSubmit} sx={{ '& .MuiTextField-root': { m: 1, width: '25ch' }, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <TextField
                label="Никнейм"
                variant="outlined"
                value={nickname}
                onChange={(e) => setNickname(e.target.value)}
                required
            />
            <TextField
                label="Лобби"
                variant="outlined"
                value={sessionId}
                onChange={(e) => setLobby(e.target.value)}
                required
            />
            <Button type="submit" variant="contained" sx={{ mt: 3 }}>
                Подключиться к сессии
            </Button>
        </Box>
    );
}

export default ConnectSessionForm;
