import React, { useState, useEffect } from 'react';

const UserCards = () => {
    const [cards, setCards] = useState([]);

    useEffect(() => {
        const fetchUserCards = async () => {
            try {
                // Предположим, что имя пользователя хранится в localStorage под ключом 'username'
                const userName = localStorage.getItem('username');
                const response = await fetch('/sessions/cards', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        // Отправляем имя пользователя в заголовке запроса
                        'Username': userName,
                    },
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                setCards(data);
            } catch (error) {
                console.error("Error fetching user cards:", error);
            }
        };

        fetchUserCards();
    }, []);

    const handleAddToBank = async (cardId) => {
        const userName = localStorage.getItem('username');
        const sessionTitle = localStorage.getItem('sessionTitle');

        try {
            const response = await fetch(`/sessions/${sessionTitle}/bank`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userName, cardId }),
            });

            if (!response.ok) {
                throw new Error('Failed to add card to bank');
            }

            // Обновляем состояние, удаляя карту, добавленную в банк
            const updatedCards = cards.filter(card => card.id !== cardId);
            setCards(updatedCards);
            console.log('Card added to bank successfully');
        } catch (error) {
            console.error('Error moving card to bank:', error);
        }
    };

    return (
        <div>
            <h2>Your Cards:</h2>
            <ul>
                {cards.map((card, index) => (
                    <li key={index}>{card.name} ({card.type})</li>
                ))}
            </ul>
        </div>
    );
};

export default UserCards;
