import React, { useState, useEffect } from 'react';
import SessionsList from './SessionsList';
import JoinSessionForm from './JoinSessionForm';
import { useNavigate } from 'react-router-dom';
import { Button, Box, Snackbar, Alert, Typography, Modal, CircularProgress, List, ListItem, ListItemText } from '@mui/material';

const modalStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    boxShadow: 24,
    p: 4,
};

const JoinSessionPage = () => {
    const navigate = useNavigate();
    const [selectedSession, setSelectedSession] = useState(null);
    const [sessionInfo, setSessionInfo] = useState(null);
    const [openModal, setOpenModal] = useState(false);

    useEffect(() => {
        if (selectedSession && selectedSession.status !== 'active') {
            setOpenModal(true);
            // Здесь можно добавить polling механизм для обновления информации о сессии
        }
    }, [selectedSession]);

    const handleJoin = (session) => {
        setSelectedSession(session);
    };

    const fetchSessionDetails = async (title) => {
        // Запрос к серверу для получения деталей сессии
        try {
            const response = await fetch(`/sessions/details/${title}`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setSessionInfo(data);
            if (data.status === 'active') {
                setOpenModal(false);
                navigate(`/game/${title}`);
            }
        } catch (error) {
            console.error("Error fetching session details:", error);
        }
    };

    const handleConfirmJoin = async (title, userName) => {
        try {
            const response = await fetch(`/sessions/join`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title, userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            await fetchSessionDetails(title);
        } catch (error) {
            console.error("Error joining session:", error);
        }
    };

    const handleCloseModal = () => setOpenModal(false);

    return (
        <div>
            <SessionsList onJoin={handleJoin}/>
            {selectedSession && <JoinSessionForm session={selectedSession} onJoin={handleConfirmJoin}/>}
            <Modal
                open={openModal}
                onClose={handleCloseModal}
                aria-labelledby="session-details-modal-title"
                aria-describedby="session-details-modal-description"
            >
                <Box sx={modalStyle}>
                    <Typography id="session-details-modal-title" variant="h6" component="h2">
                        Статус сессии: {sessionInfo?.status}
                    </Typography>
                    <Typography id="session-details-modal-description" sx={{ mt: 2 }}>
                        Игроки: {sessionInfo?.currentPlayers}/{sessionInfo?.maxPlayers}
                    </Typography>
                    <List dense>
                        {sessionInfo?.users.map((user, index) => (
                            <ListItem key={index}>
                                <ListItemText primary={user.name}/>
                            </ListItem>
                        ))}
                    </List>
                    {sessionInfo?.status === 'active' && (
                        <Button variant="contained" color="primary" onClick={() => navigate(`/game/${selectedSession.title}`)}>
                            Присоединиться к игре
                        </Button>
                    )}
                    {sessionInfo?.status !== 'active' && <CircularProgress />}
                </Box>
            </Modal>
        </div>
    );
};

export default JoinSessionPage;
