import React, { useState } from 'react';
import { Button, TextField, Box, Typography } from '@mui/material';

const CreateSessionForm = () => {
    const [title, setTitle] = useState(''); // Состояние для хранения названия сессии

    const handleSubmit = async (event) => {
        event.preventDefault(); // Предотвращение стандартного поведения формы

        try {
            const response = await fetch('/sessions', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const result = await response.json();
            console.log('Session created:', result);
            // Очистка поля формы после успешного создания сессии
            setTitle('');
            // Дальнейшие действия после создания сессии (например, перенаправление)
        } catch (error) {
            console.error('Error creating session:', error);
        }
    };

    return (
        <Box
            component="form"
            onSubmit={handleSubmit}
            noValidate
            sx={{
                marginTop: 8,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            }}
        >
            <Typography component="h1" variant="h5">
                Создать новую игровую сессию
            </Typography>
            <TextField
                variant="outlined"
                margin="normal"
                required
                fullWidth
                id="sessionName"
                label="Название сессии"
                name="sessionName"
                autoComplete="session-name"
                autoFocus
                value={title}
                onChange={(e) => setTitle(e.target.value)}
            />
            <Button
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
            >
                Создать сессию
            </Button>
        </Box>
    );
};

export default CreateSessionForm;
