import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, Button, Snackbar, Alert, Typography } from '@mui/material';
import SessionsList from './SessionsList';

const JoinSessionPage = () => {
    const navigate = useNavigate();
    const [selectedSession, setSelectedSession] = useState(null);
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');

    const handleJoin = (session) => {
        setSelectedSession(session);
    };

    const handleConfirmJoin = async (title, userName) => {
        try {
            const response = await fetch(`/sessions/join`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title, userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            console.log("Successfully joined the session");
            navigate(`/game/${title}`);
        } catch (error) {
            console.error("Error joining session:", error);
            setSnackbarMessage("Ошибка при присоединении к сессии");
            setOpenSnackbar(true);
        }
    };

    const handleBack = () => {
        if (selectedSession) {
            setSelectedSession(null);
        } else {
            navigate('/');
        }
    };

    const handleCloseSnackbar = () => setOpenSnackbar(false);

    return (
        <Box sx={{ mt: 2 }}>
            {!selectedSession ? (
                <SessionsList onJoin={handleJoin}/>
            ) : (
                <JoinSessionForm session={selectedSession} onJoin={handleConfirmJoin}/>
            )}
            <Button variant="outlined" color="primary" onClick={handleBack}>
                {selectedSession ? "Вернуться к списку сессий" : "Назад"}
            </Button>
            <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default JoinSessionPage;
