import React, { useState, useEffect } from 'react';
import SessionsList from './SessionsList';
import JoinSessionForm from './JoinSessionForm';
import { useNavigate } from 'react-router-dom';
import { Button, Box, Snackbar, Alert, Typography } from '@mui/material';

const JoinSessionPage = () => {
    const navigate = useNavigate();
    const [selectedSession, setSelectedSession] = useState(null);
    const [sessionInfo, setSessionInfo] = useState({ status: '', currentPlayers: 0, maxPlayers: 0 });
    const [openSnackbar, setOpenSnackbar] = useState(false); // Для управления видимостью Snackbar
    const [snackbarMessage, setSnackbarMessage] = useState(''); // Сообщение в Snackbar

    const handleJoin = async (session) => {
        setSelectedSession(session);
        try {
            const response = await fetch(`/sessions/details/${session.title}`);
            if (response.ok) {
                const data = await response.json();
                setSessionInfo({
                    status: data.status,
                    currentPlayers: data.users.length,
                    maxPlayers: data.maxPlayers,
                });
            } else {
                throw new Error('Не удалось загрузить детали сессии');
            }
        } catch (error) {
            console.error(error);
            setSnackbarMessage(error.message);
            setOpenSnackbar(true);
        }
    };

    const handleConfirmJoin = async (title, userName) => {
        try {
            const response = await fetch(`/sessions/join`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title, userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            navigate(`/game/${title}`);
        } catch (error) {
            console.error("Error joining session:", error);
            setSnackbarMessage("Ошибка при присоединении к сессии");
            setOpenSnackbar(true);
        }
    };

    const handleBack = () => {
        navigate('/');
    };

    const handleCloseSnackbar = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setOpenSnackbar(false);
    };

    return (
        <div>
            <SessionsList onJoin={handleJoin}/>
            {selectedSession && <JoinSessionForm session={selectedSession} onJoin={handleConfirmJoin}/>}
            <Box mt={2}>
                <Button variant="outlined" color="primary" onClick={handleBack}>
                    Назад
                </Button>
            </Box>
            {/* Информация о сессии */}
            {selectedSession && (
                <Box mt={2} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <Typography variant="h6">Статус сессии: {sessionInfo.status}</Typography>
                    <Typography variant="h6">Игроки: {sessionInfo.currentPlayers}/{sessionInfo.maxPlayers}</Typography>
                </Box>
            )}
            {/* Snackbar для показа сообщения об ошибке */}
            <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
    );
};

export default JoinSessionPage;
