import React, { useState, useEffect } from 'react';
import { Card, CardContent, Typography, Button, CardActions, List, ListItem, ListItemText, Box, Slider } from '@mui/material';

const SessionsList = ({ onJoin }) => {
    const [sessions, setSessions] = useState([]);
    const [gradientDegree, setGradientDegree] = useState(145);

    useEffect(() => {
        // Функция для загрузки списка сессий
        const fetchSessions = async () => {
            try {
                const response = await fetch('/sessions/active');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessions(data);
            } catch (error) {
                console.error("Error fetching sessions:", error);
            }
        };

        fetchSessions();
    }, []);

    const handleGradientChange = (event, newValue) => {
        setGradientDegree(newValue);
    };

    return (
        <Box>
            <Typography>Угол градиента</Typography>
            <Slider
                value={gradientAngle}
                onChange={handleGradientChange}
                aria-labelledby="gradient-slider"
                valueLabelDisplay="auto"
                min={0}
                max={360}
            />
            <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap', gap: '20px', marginTop: '20px' }}>
                {sessions.map(session => (
                    <Card
                        key={session._id}
                        sx={{
                            width: 275,
                            backgroundImage: `linear-gradient(${gradientAngle}deg, #FFC371, #FF5F6D)`,
                            color: '#fff'
                        }}
                    >
                        <CardContent>
                            <Typography variant="h5" component="div">
                                {session.title}
                            </Typography>
                            <Typography sx={{ mb: 1.5 }} color="text.secondary">
                                Игроки: {session.users.length}/{session.maxPlayers}
                            </Typography>
                            <Typography sx={{ mb: 1.5 }} color="text.secondary">
                                Статус: {session.status}
                            </Typography>
                            <List dense>
                                {session.users.map((user, index) => (
                                    <ListItem key={index}>
                                        <ListItemText primary={user.name} />
                                    </ListItem>
                                ))}
                            </List>
                        </CardContent>
                        <CardActions>
                            <Button size="small" onClick={() => onJoin(session)}>Присоединиться</Button>
                        </CardActions>
                    </Card>
                ))}
            </div>
        </Box>
    );
};

export default SessionsList;
