import React, { useState, useEffect } from 'react';

const SessionsList = ({ onJoin }) => {
    const [sessions, setSessions] = useState([]);

    useEffect(() => {
        // Функция для загрузки списка сессий
        const fetchSessions = async () => {
            try {
                const response = await fetch('/sessions/active');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessions(data);
            } catch (error) {
                console.error("Error fetching sessions:", error);
            }
        };

        fetchSessions();
    }, []);

    return (
        <div>
            <h2>Доступные Игровые Сессии</h2>
            <ul>
                {sessions.map(session => (
                    <li key={session._id}>
                        <strong>{session.title}</strong>
                        <ul>
                            {/* Проверяем, есть ли у сессии свойство users и отображаем список пользователей */}
                            {session.users && session.users.map((user, index) => (
                                <li key={index}>{user.name}</li> // Используем index как ключ, если у вас нет уникального идентификатора пользователя
                            ))}
                        </ul>
                        <button onClick={() => onJoin(session)}>Присоединиться</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default SessionsList;
