import React, { useState, useEffect } from 'react';
import {useParams} from "react-router-dom";
import { Card, CardContent, Typography, Button, Grid, CardMedia, Box } from '@mui/material';

const UserCards = () => {
    const [cards, setCards] = useState([]);
    const { title } = useParams();

    useEffect(() => {
        const fetchUserCards = async () => {
            try {
                const userName = localStorage.getItem('username');
                const response = await fetch('/sessions/cards', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        // Отправляем имя пользователя в заголовке запроса
                        'Username': userName,
                    },
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                setCards(data);
            } catch (error) {
                console.error("Error fetching user cards:", error);
            }
        };

        fetchUserCards();
    }, []);

    const handleAddToBank = async (cardId) => {
        const userName = localStorage.getItem('username');

        try {
            const response = await fetch(`/sessions/${title}/bank`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userName, cardId }),
            });

            if (!response.ok) {
                throw new Error('Failed to add card to bank');
            }

            // Обновляем состояние, удаляя карту, добавленную в банк
            const updatedCards = cards.filter(card => card.id !== cardId);
            setCards(updatedCards);
            console.log('Card added to bank successfully');
        } catch (error) {
            console.error('Error moving card to bank:', error);
        }
    };

    const handleAddToProperty = async (cardId) => {
        const userName = localStorage.getItem('username');

        try {
            const response = await fetch(`/sessions/${title}/property`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userName, cardId }),
            });

            if (!response.ok) {
                throw new Error('Failed to add card to property');
            }

            // Обновляем состояние, удаляя карту, добавленную в собственность
            const updatedCards = cards.filter(card => card.id !== cardId);
            setCards(updatedCards);
            console.log('Card added to property successfully');
        } catch (error) {
            console.error('Error moving card to property:', error);
        }
    };

    return (
        <div>
            <Typography variant="h4" gutterBottom>
                Ваши карты:
            </Typography>
            <Grid container spacing={2} justify="center">
                {cards.map((card) => (
                    <Grid item key={card.id} xs={12} sm={4} md={3} lg={2}>
                        <Card sx={{maxWidth: 345, m: 1}}>
                            <CardMedia
                                component="img"
                                height="auto"
                                image={card.image}
                                alt={card.name}
                                sx={{
                                    width: 'auto',
                                    maxHeight: 300,
                                    mx: 'auto'
                                }} // Устанавливаем максимальную высоту изображения и автоматический маржин по горизонтали для центрирования
                            />
                            <CardContent>
                                <Box display="flex" justifyContent="center" m={1}>
                                    {['money', 'action'].includes(card.type) && (
                                        <Button onClick={() => handleAddToBank(card.id)} variant="outlined"
                                                color="primary" sx={{mr: 1}}>
                                            Добавить в банк
                                        </Button>
                                    )}
                                    {card.type === 'property' && (
                                        <Button onClick={() => handleAddToProperty(card.id)} variant="outlined"
                                                color="secondary">
                                            Добавить в собственность
                                        </Button>
                                    )}
                                </Box>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </div>

    );
};

export default UserCards;
