import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import LeaveSessionButton from "./LeaveSession";
import UserCards from "./UserCards";
import DrawCards from "./DrawCards";
import { Typography, Grid, Card, CardContent, Box, CardMedia } from '@mui/material';


const GamePage = () => {
    const { title } = useParams();
    const [sessionDetails, setSessionDetails] = useState(null);

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                const response = await fetch(`/sessions/details/${title}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, [title]);

    if (!sessionDetails) {
        return <div>Загрузка деталей сессии...</div>;
    }


    return (
        <Box sx={{ flexGrow: 1, p: 3 }}>
            <Grid container spacing={3}>
                <Grid item xs={12} md={6}>
                    <Typography variant="h4" gutterBottom>
                        Игровая сессия: {sessionDetails.title}
                    </Typography>
                    <Typography variant="h5" gutterBottom>
                        Текущие участники:
                    </Typography>
                    <ul>
                        {sessionDetails.users.map((user, index) => (
                            <li key={index}>{user.name}</li>
                        ))}
                    </ul>
                    {/* Компоненты для взятия карт и отображения карт в руке игрока */}
                    <DrawCards />
                    <UserCards />
                </Grid>

                {/* Компонент для отображения карт в банке */}
                <Grid item xs={12} md={3}>
                    <Card>
                        <CardContent>
                            <Typography variant="h5" gutterBottom>
                                Банк
                            </Typography>
                            {sessionDetails.bank.map((bankEntry, index) => (
                                <Box key={index} mb={2}>
                                    <Typography variant="h6">Игрок: {bankEntry.playerId.name}</Typography>
                                    <ul>
                                        {bankEntry.cards.map((card, cardIndex) => (
                                            <li key={cardIndex}>{card.name} ({card.type})</li>
                                        ))}
                                    </ul>
                                </Box>
                            ))}
                        </CardContent>
                    </Card>
                </Grid>

                {/* Компонент для отображения карт в собственности */}
                <Grid item xs={12} md={3}>
                    <Card>
                        <CardContent>
                            <Typography variant="h5" gutterBottom>
                                Собственность
                            </Typography>
                            {sessionDetails.property.map((propertyEntry, index) => (
                                <Box key={index} mb={2}>
                                    <Typography variant="h6">Игрок: {propertyEntry.playerId.name}</Typography>
                                    <ul>
                                        {propertyEntry.cards.map((card, cardIndex) => (
                                            <li key={cardIndex}>{card.name} ({card.type})</li>
                                        ))}
                                    </ul>
                                </Box>
                            ))}
                        </CardContent>
                    </Card>
                </Grid>
            </Grid>
        </Box>
    );
}

export default GamePage;
