import React, { useEffect, useState } from 'react';
import './App.css';
import CreateSessionForm from "./frontend/CreateSessionForm";
import JoinSessionPage from "./frontend/JoinSessionPage";
import GamePage from "./frontend/GamePage"


const App = () => {

  const [state, setState] = useState(null);

  const callBackendAPI = async () => {
    const response = await fetch('/express_backend');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message)
    }
    return body;
  };

  // получение GET маршрута с сервера Express, который соответствует GET из server.js
  useEffect(() => {
    callBackendAPI()
    .then(res => setState(res.express))
    .catch(err => console.log(err));
  }, [])

    return (
        <Router>
            <div>
                <Switch>
                    <Route path="/" exact>
                        <h1>Добро пожаловать в нашу игру</h1>
                        {/* Домашняя страница или другой контент */}
                    </Route>
                    <Route path="/create-session" exact>
                        <h1>Создать новую игровую сессию</h1>
                        <CreateSessionForm />
                    </Route>
                    <Route path="/join-session" exact>
                        <JoinSessionPage />
                    </Route>
                    <Route path="/game/:title" exact>
                        <GamePage />
                    </Route>
                    <Redirect to="/" /> {/* Редирект на главную страницу, если маршрут не найден */}
                </Switch>
            </div>
        </Router>
    );
}

export default App;
