import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import LeaveSessionButton from "./LeaveSessionButton";
import UserCards from "./UserCards";

const GamePage = () => {
    const { title } = useParams();
    const [sessionDetails, setSessionDetails] = useState(null);

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                const response = await fetch(`/sessions/details/${title}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, [title]);

    if (!sessionDetails) {
        return <div>Loading session details...</div>;
    }

    return (
        <div>
            <h2>Game Session: {sessionDetails.title}</h2>
            <h3>Current Participants:</h3>
            <ul>
                {sessionDetails.users.map((user, index) => (
                    <li key={index}>{user.name}</li>
                ))}
            </ul>
            {/* Passing sessionTitle prop to LeaveSessionButton */}
            <LeaveSessionButton sessionTitle={sessionDetails.title} />
            {/* Assuming UserCards will handle displaying user's cards */}
            <UserCards />
        </div>
    );
};

export default GamePage;
