// GamePage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const GamePage = () => {
    const { title } = useParams(); // Используем название сессии из URL
    const [sessionDetails, setSessionDetails] = useState(null);

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                const response = await fetch(`/sessions/${title}`); // Обновите URL согласно вашему API
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, [title]);

    if (!sessionDetails) {
        return <div>Загрузка деталей сессии...</div>;
    }

    return (
        <div>
            <h2>Сессия: {sessionDetails.title}</h2>
            <h3>Пользователи:</h3>
            <ul>
                {sessionDetails.users.map(user => (
                    <li key={user._id}>{user.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default GamePage;
