import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import LeaveSessionButton from "./LeaveSession";
import UserCards from "./UserCards";
import DrawCards from "./DrawCards";
import { Box, Card, CardContent, Typography, CardMedia, Grid, Paper, Chip } from '@mui/material';
import PropertyCards from "./PropertyCards";



const GamePage = () => {
    const { title } = useParams();
    const [sessionDetails, setSessionDetails] = useState(null);

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                const response = await fetch(`/sessions/details/${title}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, [title]);

    if (!sessionDetails) {
        return <div>Загрузка деталей сессии...</div>;
    }


    return (
        <Box sx={{ flexGrow: 1, p: 3 }}>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <Typography variant="h4" gutterBottom>
                        Игровая сессия: {sessionDetails.title}
                    </Typography>
                    <Typography variant="h5" gutterBottom>
                        Текущие участники:
                    </Typography>
                    {sessionDetails.users.map((user, index) => (
                        <Typography key={index}>{user.name}</Typography>
                    ))}
                    <DrawCards />
                    <UserCards />
                </Grid>
            </Grid>

            {/* Компоненты для отображения карт в банке */}
            <Grid container spacing={2} sx={{ mt: 3 }}>
                <Grid item xs={12} md={6}>
                    <Paper elevation={3} sx={{ p: 2 }}>
                        <Typography variant="h5" gutterBottom>
                            Банк
                        </Typography>
                        {sessionDetails.bank.map((bankEntry, index) => (
                            <Box key={index} mb={2} sx={{ overflowX: 'auto', display: 'flex', alignItems: 'center' }}>
                                <Chip label={`Игрок: ${bankEntry.playerId.name}`} color="primary" sx={{ mr: 2 }} />
                                <Box sx={{ display: 'flex', flexDirection: 'row', gap: 1 }}>
                                    {bankEntry.cards.map((card, cardIndex) => (
                                        <CardMedia
                                            key={cardIndex}
                                            component="img"
                                            sx={{ width: 128, height: 192 }}
                                            image={card.image}
                                            alt={card.name}
                                        />
                                    ))}
                                </Box>
                            </Box>
                        ))}
                    </Paper>
                </Grid>

                {/* Компонент для отображения карт в собственности */}
                <PropertyCards sessionDetails={sessionDetails} />
            </Grid>
        </Box>
    );

}

export default GamePage;
