import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const GamePage = () => {
    const { title } = useParams();
    const [sessionDetails, setSessionDetails] = useState(null);
    const { sessionId } = useParams();
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                // Предполагается, что ваш эндпоинт ожидает название сессии в URL
                const response = await fetch(`/sessions/details/${title}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, [title]);

    const handleLeaveSession = async () => {
        try {
            const userId = 'id пользователя'; // Получите ID текущего пользователя
            await fetch(`/sessions/${sessionId}/leave`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({userId})
            });
            navigate('/'); // Перенаправление на главную страницу после выхода из сессии
        } catch (error) {
            console.error("Ошибка при выходе из сессии:", error);
        }
    }

    // Обработка состояния загрузки
    if (!sessionDetails) {
        return <div>Загрузка деталей сессии...</div>;
    }

    return (
        <div>
            <h2>Игровая сессия: {sessionDetails.title}</h2>
            <h3>Текущие участники:</h3>
            <ul>
                {sessionDetails.users.map((user, index) => (
                    <li key={index}>{user.name}</li>
                ))}
            </ul>
            <button onClick={handleLeaveSession}>Выйти из сессии</button>
        </div>
    );
}

export default GamePage;
