import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const GamePage = () => {
    const [sessionDetails, setSessionDetails] = useState(null); // Состояние для хранения деталей сессии

    useEffect(() => {
        // Функция для загрузки деталей сессии
        const fetchSessionDetails = async () => {
            try {
                // Замените URL на актуальный адрес вашего API
                const response = await fetch(`/sessions/active`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data); // Сохраняем детали сессии в состояние
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, []);

    return (
        <div>
            <h2>Игровая сессия: {sessionDetails.title}</h2>
            <h3>Пользователи в сессии:</h3>
            <ul>
                {sessionDetails.users.map((user, index) => (
                    <li key={index}>{user.name}</li> // Предполагаем, что у пользователя есть свойство name
                ))}
            </ul>
        </div>
    );
}

export default GamePage;
