import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Box, TextField, Button, Typography, Snackbar, Alert } from '@mui/material';
import SessionsList from './SessionsList';

const JoinSessionPage = () => {
    const navigate = useNavigate();
    const [userName, setUserName] = useState('');
    const [selectedSession, setSelectedSession] = useState(null);
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');

    useEffect(() => {
        if (selectedSession) {
            handleSubmit();
        }
    }, [selectedSession]);

    const handleJoin = (session) => {
        setSelectedSession(session);
    };

    const handleSubmit = async () => {
        if (!selectedSession) return;
        try {
            const response = await fetch(`/sessions/join`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title: selectedSession.title, userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            console.log("Successfully joined the session");
            navigate(`/game/${selectedSession.title}`);
        } catch (error) {
            console.error("Error joining session:", error);
            setSnackbarMessage("Ошибка при присоединении к сессии");
            setOpenSnackbar(true);
        }
    };

    const handleBack = () => {
        navigate('/');
    };

    const handleCloseSnackbar = () => {
        setOpenSnackbar(false);
    };

    return (
        <div>
            <SessionsList onJoin={handleJoin}/>
            {selectedSession && (
                <Box
                    component="form"
                    noValidate
                    autoComplete="off"
                    onSubmit={handleSubmit}
                    sx={{ '& .MuiTextField-root': { m: 1, width: '25ch' }, mt: 3 }}
                >
                    <Typography variant="h6">Присоединиться к игровой сессии: {selectedSession.title}</Typography>
                    <TextField
                        required
                        id="userName"
                        label="Ваше имя"
                        value={userName}
                        onChange={(e) => setUserName(e.target.value)}
                    />
                    <Button type="submit" variant="contained" sx={{ mt: 2 }}>Присоединиться</Button>
                </Box>
            )}
            <Button variant="outlined" onClick={handleBack} sx={{ mt: 2 }}>Назад</Button>
            <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity="error">
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
    );
};

export default JoinSessionPage;
