import React, { useState, useEffect } from 'react';
import {useParams} from "react-router-dom";
import { Box, Typography, Card, CardActionArea, Button, CardMedia } from '@mui/material';

const UserCards = () => {
    const [cards, setCards] = useState([]);
    const { title } = useParams();

    useEffect(() => {
        const fetchUserCards = async () => {
            try {
                const userName = localStorage.getItem('username');
                const response = await fetch('/sessions/cards', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        // Отправляем имя пользователя в заголовке запроса
                        'Username': userName,
                    },
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }

                const data = await response.json();
                setCards(data);
            } catch (error) {
                console.error("Error fetching user cards:", error);
            }
        };

        fetchUserCards();
    }, []);

    const handleAddToBank = async (cardId) => {
        const userName = localStorage.getItem('username');

        try {
            const response = await fetch(`/sessions/${title}/bank`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userName, cardId }),
            });

            if (!response.ok) {
                throw new Error('Failed to add card to bank');
            }

            // Обновляем состояние, удаляя карту, добавленную в банк
            const updatedCards = cards.filter(card => card.id !== cardId);
            setCards(updatedCards);
            console.log('Card added to bank successfully');
        } catch (error) {
            console.error('Error moving card to bank:', error);
        }
    };

    const handleAddToProperty = async (cardId) => {
        const userName = localStorage.getItem('username');

        try {
            const response = await fetch(`/sessions/${title}/property`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ userName, cardId }),
            });

            if (!response.ok) {
                throw new Error('Failed to add card to property');
            }

            // Обновляем состояние, удаляя карту, добавленную в собственность
            const updatedCards = cards.filter(card => card.id !== cardId);
            setCards(updatedCards);
            console.log('Card added to property successfully');
        } catch (error) {
            console.error('Error moving card to property:', error);
        }
    };

    return (
        <div>
            <Typography variant="h4" gutterBottom>
                Ваши карты:
            </Typography>
            <Box display="flex" flexWrap="wrap" justifyContent="center" gap={2}>
                {cards.map((card) => (
                    <Box key={card.id} position="relative">
                        <CardActionArea onClick={() => handleSelectCard(card.id)}>
                            <Card sx={{ maxWidth: 345 }}>
                                <CardContent>
                                    <img src={card.image} alt={card.name} style={{ width: '100%', height: 'auto' }} />
                                </CardContent>
                            </Card>
                        </CardActionArea>
                        {cards === card.id && (
                            <Box display="flex" justifyContent="center" position="absolute" top={0} left={0} right={0} zIndex="modal">
                                {['money', 'action'].includes(card.type) && (
                                    <Button onClick={() => handleAddToBank(card.id)} variant="contained" color="primary" size="small" sx={{ m: 0.5 }}>
                                        В банк
                                    </Button>
                                )}
                                {card.type === 'property' && (
                                    <Button onClick={() => handleAddToProperty(card.id)} variant="contained" color="secondary" size="small" sx={{ m: 0.5 }}>
                                        В собственность
                                    </Button>
                                )}
                            </Box>
                        )}
                    </Box>
                ))}
            </Box>
        </div>
    );
};

export default UserCards;
