// DrawCards.js
import React, {useState} from 'react';
import { useParams } from 'react-router-dom';
import { Button, Zoom, Box, Grid } from '@mui/material';

const DrawCards = () => {
    const { title } = useParams(); // Используем название сессии из URL
    const [cards, setCards] = useState([]);
    const [hovered, setHovered] = useState(false);

    const handleDrawCards = async () => {
        const userName = localStorage.getItem('username'); // Предполагается, что имя пользователя сохранено в localStorage

        try {
            const response = await fetch(`/sessions/${title}/draw`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            // Обработка успешного взятия карт
            const data = await response.json();
            setCards(data);
        } catch (error) {
            console.error('Error drawing cards:', error);
        }
    };

    return (
        <Grid container spacing={2}>
            <Grid item xs={12}>
                <Box
                    sx={{
                        display: 'flex',
                        alignItems: 'flex-start',
                        flexDirection: 'column',
                        ml: 2, // Добавляем немного отступа слева
                    }}
                    onMouseEnter={() => setHovered(true)}
                    onMouseLeave={() => setHovered(false)}
                >
                    <Zoom in={true} style={{ transitionDelay: hovered ? '500ms' : '0ms' }}>
                        <img
                            src={`${process.env.PUBLIC_URL}/images/back.png`}
                            alt="Колода карт"
                            style={{
                                maxWidth: '100px',
                                height: 'auto',
                                marginBottom: '10px',
                                transform: hovered ? 'scale(1.1)' : 'scale(1)',
                                transition: 'transform 0.3s'
                            }}
                        />
                    </Zoom>
                    <Zoom in={true} style={{ transitionDelay: hovered ? '500ms' : '0ms' }}>
                        <Button
                            variant="contained"
                            color="primary"
                            onClick={handleDrawCards}
                            sx={{ transform: hovered ? 'scale(1.1)' : 'scale(1)' }}
                            style={{ transition: 'transform 0.3s' }}
                        >
                            Взять две карты из колоды
                        </Button>
                    </Zoom>
                </Box>
            </Grid>
        </Grid>
    );
};


export default DrawCards;
