// DrawCards.js
import React from 'react';
import { useParams } from 'react-router-dom';

const DrawCards = ({ updateGame }) => {
    const { title } = useParams(); // Используем название сессии из URL

    const handleDrawCards = async () => {
        const userName = localStorage.getItem('username'); // Предполагается, что имя пользователя сохранено в localStorage

        try {
            const response = await fetch(`/sessions/${title}/draw-cards`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            // Обработка успешного взятия карт
            console.log('Cards drawn successfully');
            // Вызов функции обновления состояния игры, если таковая передается
            if (updateGame) updateGame();
        } catch (error) {
            console.error('Error drawing cards:', error);
        }
    };

    return (
        <button onClick={handleDrawCards}>Взять карты</button>
    );
};

export default DrawCards;
