import React, { useState } from 'react';
import SessionsList from './SessionsList';
import JoinSessionForm from './JoinSessionForm';

const JoinSessionPage = () => {
    const [selectedSession, setSelectedSession] = useState(null);

    const handleJoin = (session) => {
        setSelectedSession(session);
    };

    const handleConfirmJoin = async (title, userName) => {
        try {
            const response = await fetch(`/sessions/join`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ sessionId: title, userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            console.log("Successfully joined the session");
            setSelectedSession(null);
            // Дополнительная логика после успешного присоединения
        } catch (error) {
            console.error("Error joining session:", error);
        }
    };

    return (
        <div>
            <SessionsList onJoin={handleJoin} />
            {selectedSession && <JoinSessionForm session={selectedSession} onJoin={handleConfirmJoin} />}
        </div>
    );
};

export default JoinSessionPage;
