import React, { useState, useEffect } from 'react';
import SessionsList from './SessionsList';
import JoinSessionForm from './JoinSessionForm';
import { useNavigate } from 'react-router-dom';
import { Button, Box, Snackbar, Alert, CircularProgress, Typography } from '@mui/material';

const JoinSessionPage = () => {
    const navigate = useNavigate();
    const [selectedSession, setSelectedSession] = useState(null);
    const [sessionStatus, setSessionStatus] = useState('');
    const [openSnackbar, setOpenSnackbar] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');

    const handleJoin = (session) => {
        setSelectedSession(session);
    };

    const handleConfirmJoin = async (title, userName) => {
        try {
            const response = await fetch(`/sessions/join`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ title, userName }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }

            const data = await response.json();
            setSessionStatus(data.session.status); // Обновляем статус сессии после присоединения

            if (data.session.status === 'active') {
                navigate(`/game/${title}`);
            } else {
                // Отображаем информацию об ожидании начала игры
                setSnackbarMessage("Игра скоро начнется. Ожидайте...");
                setOpenSnackbar(true);
            }
        } catch (error) {
            console.error("Error joining session:", error);
            setSnackbarMessage("Ошибка при присоединении к сессии");
            setOpenSnackbar(true);
        }
    };

    const handleBack = () => navigate('/');

    const handleCloseSnackbar = () => setOpenSnackbar(false);

    return (
        <div>
            <SessionsList onJoin={handleJoin}/>
            {selectedSession && <JoinSessionForm session={selectedSession} onJoin={handleConfirmJoin}/>}
            <Box mt={2}>
                <Button variant="outlined" color="primary" onClick={handleBack}>
                    Назад
                </Button>
            </Box>
            {sessionStatus !== 'active' && (
                <Box mt={2} sx={{ display: 'flex', justifyContent: 'center' }}>
                    <CircularProgress />
                    <Typography ml={2}>Ожидание начала игры...</Typography>
                </Box>
            )}
            <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
                <Alert onClose={handleCloseSnackbar} severity="info" sx={{ width: '100%' }}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>
        </div>
    );
};

export default JoinSessionPage;
