import React, { useState } from 'react';
import { Box, TextField, Button, Typography, Modal, CircularProgress, List, ListItem, ListItemText } from '@mui/material';

// Стили для модального окна
const modalStyle = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    boxShadow: 24,
    p: 4,
};

const JoinSessionForm = ({ session, onJoin, sessionInfo, openModal, setOpenModal }) => {
    const [userName, setUserName] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();
        onJoin(session.title, userName);

        // Сохранение имени пользователя в localStorage после успешного входа
        localStorage.setItem('username', userName);
    };

    // Функция для закрытия модального окна
    const handleCloseModal = () => setOpenModal(false);

    return (
        <>
            <Box
                component="form"
                sx={{
                    '& .MuiTextField-root': { m: 1, width: '25ch' },
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    marginTop: '20px'
                }}
                noValidate
                autoComplete="off"
                onSubmit={handleSubmit}
            >
                <Typography variant="h6" component="h2" gutterBottom>
                    Присоединиться к игровой сессии
                </Typography>
                <TextField
                    required
                    id="userName"
                    label="Ваше имя"
                    value={userName}
                    onChange={(e) => setUserName(e.target.value)}
                />
                <Button type="submit" variant="contained" color="primary" sx={{ mt: 3 }}>
                    Присоединиться к сессии
                </Button>
            </Box>
            <Modal
                open={openModal}
                onClose={handleCloseModal}
                aria-labelledby="session-status-modal-title"
                aria-describedby="session-status-modal-description"
            >
                <Box sx={modalStyle}>
                    <Typography id="session-status-modal-title" variant="h6" component="h2">
                        Статус сессии: {sessionInfo?.status}
                    </Typography>
                    <Typography id="session-status-modal-description" sx={{ mt: 2 }}>
                        Игроки: {sessionInfo?.currentPlayers}/{sessionInfo?.maxPlayers}
                    </Typography>
                    <List dense>
                        {sessionInfo?.users.map((user, index) => (
                            <ListItem key={index}>
                                <ListItemText primary={user.name}/>
                            </ListItem>
                        ))}
                    </List>
                    {sessionInfo?.status !== 'active' && <CircularProgress />}
                </Box>
            </Modal>
        </>
    );
};

export default JoinSessionForm;
