import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import LeaveSession from "./LeaveSession";
import LeaveSessionButton from "./LeaveSession";

const GamePage = () => {
    const { title } = useParams();
    const [sessionDetails, setSessionDetails] = useState(null);

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                // Предполагается, что ваш эндпоинт ожидает название сессии в URL
                const response = await fetch(`/sessions/details/${title}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };
        useEffect(() => {
            const title = "Название вашей сессии"; // Получите название текущей сессии
            fetchSessionDetails(title).then(setSessionDetails);
        }, []);

        fetchSessionDetails();
    }, [title]);



    const currentPlayerId = localStorage.getItem('userId'); // или как вы получаете ID текущего пользователя

    const currentPlayerCards = sessionDetails?.playerCards.find(p => p.playerId === currentPlayerId)?.cards;

    // Обработка состояния загрузки
    if (!sessionDetails) {
        return <div>Загрузка деталей сессии...</div>;
    }

    return (
        <div>
            <h2>Игровая сессия: {sessionDetails.title}</h2>
            <h3>Текущие участники:</h3>
            <ul>
                {sessionDetails.users.map((user, index) => (
                    <li key={index}>{user.name}</li>
                ))}
            </ul>
            <LeaveSessionButton sessionTitle={sessionDetails.title}/>
            <h2>Ваши карты:</h2>
            <ul>
                {currentPlayerCards?.map((card, index) => (
                    <li key={index}>{card.name} - {card.type}</li>
                ))}
            </ul>
        </div>
    );
}

export default GamePage;
