import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import LeaveSessionButton from "./LeaveSession";

const GamePage = () => {
    const { title } = useParams();
    const [sessionDetails, setSessionDetails] = useState(null);

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                const response = await fetch(`/sessions/details/${title}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, [title]);

    const moveToBank = async (cardId) => {
        const userName = localStorage.getItem('username');
        // Используйте реальное название вашей сессии, возможно, получив его из состояния
        const { title } = useParams();

        try {
            const response = await fetch(`/sessions/${title}/bank`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ userName, cardId }),
            });

            if (!response.ok) throw new Error('Failed to add card to bank');

            console.log('Card added to bank successfully');
            // Здесь может быть код для обновления состояния UI
            // Возможно, потребуется повторный запрос к API для получения обновлённых данных о сессии
        } catch (error) {
            console.error('Error moving card to bank:', error);
        }
    };

    if (!sessionDetails) {
        return <div>Loading session details...</div>;
    }

    return (
        <div>
            <h2>Game Session: {sessionDetails.title}</h2>
            <h3>Current Participants:</h3>
            <ul>
                {sessionDetails.users.map((user, index) => (
                    <li key={index}>{user.name}</li>
                ))}
            </ul>
            <LeaveSessionButton sessionTitle={sessionDetails.title} />
            {/* Здесь может быть компонент UserCards, который включает логику moveToBank */}
        </div>
    );
}

export default GamePage;
