import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {

  const [state, setState] = useState(null);

  const callBackendAPI = async () => {
    const response = await fetch('/express_backend');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message)
    }
    return body;
  };

  // получение GET маршрута с сервера Express, который соответствует GET из server.js
  useEffect(() => {
    callBackendAPI()
    .then(res => setState(res.express))
    .catch(err => console.log(err));
  }, [])

  return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Edit <code>src/App.js</code> and save to reload.</p>
          <a
              className="App-link"
              href="https://reactjs.org"
              target="_blank"
              rel="noopener noreferrer"
          >
            Learn React
          </a>
          {/* Кнопка для подключения к лобби */}
          <button
              className="App-link" // Использование того же класса для стиля
              onClick={() => console.log('Подключение к лобби...')}
              style={{ marginTop: '20px', padding: '10px' }} // Немного стиля для кнопки
          >
            Подключиться к лобби
          </button>
        </header>

        {/* вывод данных, полученных с сервера Express */}
        <div>
          {state}
        </div>
      </div>
  );
}

export default App;
