import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import LeaveSessionButton from "./LeaveSession";
import UserCards from "./UserCards";
import BankCards from "./BankCards"; // Предполагаем, что такой компонент существует

const GamePage = () => {
    const { title } = useParams();
    const [sessionDetails, setSessionDetails] = useState(null);

    useEffect(() => {
        const fetchSessionDetails = async () => {
            try {
                const response = await fetch(`/sessions/details/${title}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessionDetails(data);
            } catch (error) {
                console.error("Error fetching session details:", error);
            }
        };

        fetchSessionDetails();
    }, [title]);

    if (!sessionDetails) {
        return <div>Загрузка деталей сессии...</div>;
    }

    // Группировка карт по playerId
    const bankByPlayerId = sessionDetails.bank.reduce((acc, bankEntry) => {
        acc[bankEntry.playerId] = acc[bankEntry.playerId] || [];
        acc[bankEntry.playerId].push(...bankEntry.cards);
        return acc;
    }, {});

    return (
        <div>
            <h2>Игровая сессия: {sessionDetails.title}</h2>
            <h3>Текущие участники:</h3>
            <ul>
                {sessionDetails.users.map((user, index) => (
                    <li key={index}>{user.name}</li>
                ))}
            </ul>
            <UserCards />

            {/* Компонент для отображения карт в банке */}
            {Object.entries(bankByPlayerId).map(([playerId, cards], index) => (
                <div key={index}>
                    <h3>Банк игрока {playerId}:</h3>
                    <ul>
                        {cards.map((card, cardIndex) => (
                            <li key={cardIndex}>{card.name} ({card.type})</li>
                        ))}
                    </ul>
                </div>
            ))}
            <LeaveSessionButton sessionTitle={sessionDetails.title}/>
        </div>
    );
}

export default GamePage;
