import React, { useState, useEffect } from 'react';
import {Card, CardContent, Typography, Button, CardActions, List, ListItem, ListItemText, Box} from '@mui/material';

const SessionsList = ({ onJoin }) => {
    const [sessions, setSessions] = useState([]);
    const [sessionInfo, setSessionInfo] = useState({ status: '', currentPlayers: 0, maxPlayers: 0 });

    useEffect(() => {
        // Функция для загрузки списка сессий
        const fetchSessions = async () => {
            try {
                const response = await fetch('/sessions/active');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setSessions(data);
            } catch (error) {
                console.error("Error fetching sessions:", error);
            }
        };

        fetchSessions();
    }, []);

    const handleJoin = async (session) => {
        setSelectedSession(session);
        try {
            const response = await fetch(`/sessions/details/${session.title}`);
            if (response.ok) {
                const data = await response.json();
                setSessionInfo({
                    status: data.status,
                    currentPlayers: data.users.length,
                    maxPlayers: data.maxPlayers,
                });
            } else {
                throw new Error('Не удалось загрузить детали сессии');
            }
        } catch (error) {
            console.error(error);
            setSnackbarMessage(error.message);
            setOpenSnackbar(true);
        }
    };

    return (
        <div style={{display: 'flex', flexDirection: 'row', flexWrap: 'wrap', gap: '20px', marginTop: '20px'}}>
            {sessions.map(session => (
                <Card key={session._id} sx={{width: 275}}>
                    <CardContent>
                        <Typography variant="h5" component="div">
                            {session.title}
                        </Typography>
                        <Typography sx={{mb: 1.5}} color="text.secondary">
                            Игроки:
                        </Typography>
                        <List dense>
                            {session.users && session.users.map((user, index) => (
                                <ListItem key={index}>
                                    <ListItemText primary={user.name}/>
                                </ListItem>
                            ))}
                        </List>
                    </CardContent>
                    <CardActions>
                        <Button size="small" onClick={() => onJoin(session)}>Присоединиться</Button>
                    </CardActions>
                </Card>
            ))}
            {/* Информация о сессии */}
            {selectedSession && (
                <Box mt={2} sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                    <Typography variant="h6">Статус сессии: {sessionInfo.status}</Typography>
                    <Typography variant="h6">Игроки: {sessionInfo.currentPlayers}/{sessionInfo.maxPlayers}</Typography>
                </Box>
            )}
        </div>

    );
};

export default SessionsList;
