import React, { useState } from 'react';
import { Button, Modal, Form, Input } from 'antd';


const App = () => {

  /*const [state, setState] = useState(null);

  const callBackendAPI = async () => {
    const response = await fetch('/express_backend');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message)
    }
    return body;
  };

  // получение GET маршрута с сервера Express, который соответствует GET из server.js
  useEffect(() => {
    callBackendAPI()
    .then(res => setState(res.express))
    .catch(err => console.log(err));
  }, []) */

    const [isModalVisible, setIsModalVisible] = useState(0);
    const [form] = Form.useForm();

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = async () => {
        try {
            const values = await form.validateFields();
            console.log('Received values of form: ', values);
            // Здесь код для отправки данных на сервер и обработки ответа
            setIsModalVisible(false);
        } catch (errorInfo) {
            console.log('Failed:', errorInfo);
        }
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

    return (
        <div className="App">
            <Button type="primary" onClick={showModal}>
                Подключиться к сессии
            </Button>
            <Modal title="Подключение к сессии" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
                <Form form={form} layout="vertical" name="userForm">
                    <Form.Item name="nickname" label="Никнейм" rules={[{ required: true, message: 'Пожалуйста, введите ваш никнейм!' }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item name="lobby" label="Название лобби" rules={[{ required: true, message: 'Пожалуйста, введите название лобби!' }]}>
                        <Input />
                    </Form.Item>
                </Form>
            </Modal>
        </div>
    );
};

export default App;
